#include <vector>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <string.h>
#include <mpi.h>

using namespace std;

vector<char> buffer;
int amount[26];
int indexes[26][16093];
long matches = 0;
long start;
const int LIMIT = 7;
unsigned long long a;

char hamlet[] = {""};

void check(int offset, int current)   //offset = number of matched chars, current = current number it's on
{
    int i;

    if(offset > 0)
    {
        if(buffer[offset] == hamlet[current + offset])
        {
            check(offset + 1, current);
        }
        else
        {
            if(offset >= LIMIT)
            {
		static int last = 0;
		int checked = a - last;

                MPI_Send(&buffer.front(), offset, MPI_CHAR, 0, 0, MPI_COMM_WORLD);   //tag: 0 for matches
                MPI_Send(&checked, 1, MPI_UNSIGNED_LONG_LONG, 0, 1, MPI_COMM_WORLD);    //tag: 1 for number of chars checked so far
		last = a;
            }
        }
    }
    else
    {
        for(i = 0; i < amount[buffer[0] - 97]; ++i)
        {
            check(offset + 1, indexes[buffer[0] - 97][i]);
        }
    }
}

char generate()
{
    return (rand() % 26 + 97);
}

int main(int argc, char **argv)
{
    int size, rank;
    MPI_Init(&argc, &argv);
    MPI_Comm_size(MPI_COMM_WORLD, &size);
    MPI_Comm_rank(MPI_COMM_WORLD, &rank);

    if(rank == 0)
    {
        a = 0;
        unsigned long long temp;
        start = time(NULL);
        printf("Displaying matches of %d+ characters.\n", LIMIT);
        printf("Running on %d processors.\n", size);
        char input[50];

        while(1)
        {
            int i;

            for(i = 0; i < 50; ++i)
            {
                input[i] = '\0';
            }

            MPI_Recv(input, 50, MPI_CHAR, MPI_ANY_SOURCE, 0, MPI_COMM_WORLD, MPI_STATUS_IGNORE);                //match
            matches++;
            MPI_Recv(&temp, 1, MPI_UNSIGNED_LONG_LONG, MPI_ANY_SOURCE, 1, MPI_COMM_WORLD, MPI_STATUS_IGNORE);   //number of chars checked
            a += temp;

            FILE *ofp = fopen("output.txt", "a");
            fprintf(ofp, "%s\n", input);
            fclose(ofp);

            printf("%s\n", input);

            float runningTime = (float)(time(NULL) - start) / 60;
            float matchesPerMin = (float)matches / runningTime;

            printf("%.2f matches/min. %ld matches found in total. Running for %.2f minutes.\n", matchesPerMin, matches, runningTime);

            long charsPerMatch = a / matches;
            long charsPerMin = a / (time(NULL) - start);
            printf("%ld characters per second. %llu characters generated. One match per %ld characters. \n", charsPerSec, a, charsPerMatch);
        }
    }
    else
    {
        srand((unsigned)time(NULL) + rank);

        for(a = 0; a < 1000; ++a)
        {
            buffer.push_back(generate());
        }

        for (a = 0; a < 26; ++a)
        {
            amount[a] = 0;
        }

        for(a = 0; a < strlen(hamlet); ++a)
        {
            indexes[hamlet[a] - 97][amount[hamlet[a] - 97]] = a;
            amount[hamlet[a] - 97]++;
        }

        for(a = 0;; ++a)
        {
            check(0, 0);

            buffer.erase(buffer.begin());
            buffer.push_back(generate());
        }
    }

    MPI_Finalize();
    return 0;
}
